import requests, os, re, time, sys
from random import randint

# 检查命令行参数
if len(sys.argv) < 3:
    print("使用方法: python main.py <用户名> <密码>")
    print("示例: python main.py user@email.com mypassword")
    print("多账号示例: python zepplife.py user1@email.com#user2@email.com pass1#pass2")
    sys.exit(1)

# 从命令行参数获取用户名和密码
user_input = sys.argv[1]        # 用户名（支持多个用#分隔）
password_input = sys.argv[2]    # 密码（支持多个用#分隔）

# 步数最小值
minSteps = 19760
# 步数最大值
maxSteps = 26240
# 账号登录简单记录下Token，运行时自动刷新可避免频繁请求
isLog = True
# 有批量登录批量记录Token需求的可以自己改写避免频繁请求
# Token的有效期好像是10天左右，尽量每次运行都去刷新一下
current_dir = os.path.dirname(os.path.abspath(__file__))


def get_token_file_path(username):
    """为每个账号生成唯一的token文件路径"""
    import hashlib
    # 使用MD5哈希用户名，避免文件名中的特殊字符
    username_hash = hashlib.md5(username.encode('utf-8')).hexdigest()[:8]
    filename = f"token_{username_hash}.txt"
    return os.path.join(current_dir, filename)


def logToken(lt, username):
    """保存token到用户专属文件"""
    file_path = get_token_file_path(username)
    with open(file_path, "w", encoding="utf-8") as f:
        f.write(lt)


def readToken(username):
    """读取用户专属的token"""
    file_path = get_token_file_path(username)
    if os.path.isfile(file_path):
        with open(file_path, "r", encoding="utf-8") as f:
            lt = f.read().strip()
            if lt:
                return lt
            else:
                return False
    else:
        return False


def login(uu, pwd):
    PHONE_PATTERN = r"(^(1)\d{10}$)"
    if re.match(PHONE_PATTERN, uu):
        uu = f"+86{uu}"
        third_name = "huami_phone"
    else:
        third_name = "huami"

    headers = {
        "content-type": "application/x-www-form-urlencoded; charset=UTF-8",
        "user-agent": "MiFit/6.12.0 (MCE16; Android 16; Density/1.5)",
        "app_name": "com.xiaomi.hm.health",
    }
    url1 = "https://api-user.huami.com/registrations/" + uu + "/tokens"
    data1 = f"client_id=HuaMi&country_code=CN&json_response=true&name={uu}&password={pwd}&redirect_uri=https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html&state=REDIRECTION&token=access"
    res1 = requests.post(url1, data=data1, headers=headers)

    if res1.status_code == 200:
        res1 = res1.json()
        if "access" in res1:
            code = res1["access"]
        else:
            print(f"------ 用户名或密码错误，请变更后再试 ------")
            return
    elif res1.status_code == 429:
        print(f"------ 请求过于频繁，请变更IP或稍后再试 ------")
        return
    else:
        print(f"------ Access Code：{res1.status_code}，账号登录失败 ------")
        return

    url2 = "https://account.huami.com/v2/client/login"
    data2 = f"app_name=com.xiaomi.hm.health&country_code=CN&code={code}&device_id=02:00:00:00:00:00&device_model=android_phone&app_version=6.12.0&grant_type=access_token&allow_registration=false&source=com.xiaomi.hm.health&third_name={third_name}"
    res2 = requests.post(url2, data=data2, headers=headers)

    if res2.status_code == 200:
        res2 = res2.json()
        if isLog == True:
            logToken(res2["token_info"]["login_token"], uu)
        token_info = {
            "user_id": res2["token_info"]["user_id"],
            "app_token": res2["token_info"]["app_token"],
        }
        return token_info
    else:
        print(f"------ Login Code：{res2.status_code}，账号登录失败 ------")
        return False


def getToken(lt):
    headers = {
        "content-type": "application/x-www-form-urlencoded; charset=UTF-8",
        "user-agent": "MiFit/6.12.0 (MCE16; Android 16; Density/1.5)",
        "app_name": "com.xiaomi.hm.health",
    }
    url1 = f"https://account-cn.huami.com/v1/client/renew_login_token?login_token={lt}"
    res1 = requests.get(url1, headers=headers)

    res1 = res1.json()
    if "token_info" in res1:
        logToken(res1["token_info"]["login_token"])

    url2 = f"https://account-cn.huami.com/v1/client/app_tokens?login_token={lt}"
    res2 = requests.get(url2, headers=headers)

    res2Code = res2.status_code
    res2 = res2.json()
    print(res2)
    if "token_info" in res2:
        token_info = {
            "user_id": res2["token_info"]["user_id"],
            "app_token": res2["token_info"]["app_token"],
        }
        return token_info
    else:
        print(f"------ Token 获取失败， ------")
        return False


